def find_previous_permutation(x):
    x = list(x)
    i = len(x) - 1
    while i > 0 and x[i-1] <= x[i]:
        i -= 1
    if i <= 0:
        return ''.join(x)

    j = len(x) - 1
    while x[j] >= x[i-1]:
        j -= 1
    x[i-1], x[j] = x[j], x[i-1]

    x[i:] = reversed(x[i:])
    return ''.join(x)

def count_ways(a, x):
    dp = [1] + [0] * len(a)
    for digit in x:
        for i in range(len(a) - 1, -1, -1):
            if a[i] == digit:
                dp[i+1] += dp[i]
    return str(dp[len(a)])

