# Read data from file
with open('cetate.in', 'r') as file:
    c = int(file.readline())
    N, M, K = map(int, file.readline().split())
    matrix = [list(map(int, file.readline().split())) for _ in range(N)]

# Calculate prefix sums
prefix_sum = [[0] * (M + 1) for _ in range(N + 1)]
for i in range(1, N + 1):
    for j in range(1, M + 1):
        prefix_sum[i][j] = prefix_sum[i - 1][j] + prefix_sum[i][j - 1] - prefix_sum[i - 1][j - 1] + matrix[i - 1][j - 1]

# Function to compute the sum of a submatrix using prefix sums
def submatrix_sum(x1, y1, x2, y2):
    return prefix_sum[x2][y2] - prefix_sum[x2][y1 - 1] - prefix_sum[x1 - 1][y2] + prefix_sum[x1 - 1][y1 - 1]

# Initialize variables to store the answer
max_profit = -float('inf')
config = (0, 0, 0, 0)

def task_1():
    global max_profit, config

    # Traverse all possible submatrices with sides equal to K
    for i in range(1, N - K + 2):
        for j in range(1, M - K + 2):
            profit = submatrix_sum(i, j, i + K - 1, j + K - 1)
            if profit > max_profit:
                max_profit = profit
                config = (i, j, i + K - 1, j + K - 1)

def task_2():
    global max_profit, config

    # Traverse all possible submatrices with sides at most equal to K
    for x in range(1, K + 1):
        for y in range(1, K + 1):
            for i in range(1, N - x + 2):
                for j in range(1, M - y + 2):
                    profit = submatrix_sum(i, j, i + x - 1, j + y - 1)
                    if profit > max_profit:
                        max_profit = profit
                        config = (i, j, i + x - 1, j + y - 1)

# Solve the task based on c
if c == 1:
    task_1()
elif c == 2:
    task_2()

# Write answer to file
with open('cetate.out', 'w') as file:
    file.write(f"{max_profit}\n{' '.join(map(str, config))}\n")
