def max_black_squares(matrix, N, M):
    max_count = 0
    for row in matrix:
        count = 0
        for col in row:
            if col == 0:
                count += 1
                max_count = max(max_count, count)
            else:
                count = 0
    return max_count

def lightning_height_and_count(matrix, N, M):
    visited = [[False] * M for _ in range(N)]
    def dfs(r, c):
        if r < 0 or r >= N or c < 0 or c >= M or matrix[r][c] == 0 or visited[r][c]:
            return 0
        visited[r][c] = True
        height = 1 + max(dfs(r+1, c), dfs(r+1, c-1), dfs(r+1, c+1))
        return height

    max_height = 0
    lightning_count = 0
    for r in range(N):
        for c in range(M):
            if matrix[r][c] == 1 and not visited[r][c]:
                height = dfs(r, c)
                max_height = max(max_height, height)
                lightning_count += 1
    return lightning_count, max_height

with open('foto.in', 'r') as fin:
    C = int(fin.readline().strip())
    N, M = map(int, fin.readline().split())
    matrix = [list(map(int, fin.readline().split())) for _ in range(N)]

if C == 1:
    result = max_black_squares(matrix, N, M)
elif C == 2:
    result = lightning_height_and_count(matrix, N, M)

with open('foto.out', 'w') as fout:
    if C == 1:
        fout.write(str(result) + '\n')
    elif C == 2:
        fout.write(' '.join(map(str, result)) + '\n')
