# Read 'sir.in' file
with open('sir.in', 'r') as file:
    C = int(file.readline().strip())
    N = int(file.readline().strip())
    sir = list(map(int, file.readline().strip().split()))

# Process according to the type of query
if C == 1:
    max_right = sir[-1]
    for i in range(N - 2, -1, -1):
        if sir[i] > max_right:
            answer = i + 1
            break
else:
    answer = []
    if C == 2:
        max_left = sir[0]
        answer.append(1)
        for i in range(1, N):
            if sir[i] > max_left:
                max_left = sir[i]
                answer.append(i + 1)
    elif C == 3:
        max_value = max(sir)
        first_max = sir.index(max_value)
        last_max = N - sir[::-1].index(max_value) - 1
        total = sum(max_value - sir[i] for i in range(first_max, last_max + 1))
        answer = total

# Write 'sir.out' file
with open('sir.out', 'w') as file:
    if isinstance(answer, list):
        file.write(' '.join(map(str, answer)))
    else:
        file.write(str(answer))
