#include <iostream>
#include <fstream>
#include <vector>

using namespace std;

int main() {
    ifstream in("concurs.in");
    ofstream out("concurs.out");

    int h, n;
    vector<int> premii;

    in >> h >> n;
    for (int i = 0; i < n; i++) {
        int premiu;
        in >> premiu;
        premii.push_back(premiu);
    }

    bool premiat = false;
    int nr_oras = 0;
    int nr_scoala = 0;
    for (int premiu : premii) {
        if (premiu == h) {
            premiat = true;
        }
        if (premiu / 100 == h / 100) {
            nr_oras++;
        }
        if (premiu / 10 == h / 10) {
            nr_scoala++;
        }
    }

    out << (premiat ? "DA" : "NU") << endl;
    out << nr_oras << endl;
    out << nr_scoala << endl;

    return 0;
}
