#include <fstream>
#include <cmath>

using namespace std;

int main() {
  ifstream fin("ron.in");
  ofstream fout("ron.out");

  int k, rl, rs;
  fin >> k >> rl >> rs;

  int ans = -1;
  for (int i = 1; i <= k; i++) {
    if (ceil(k / i) == i) {
      ans = i;
      break;
    }
  }

  if (ans == rl && ans == rs) {
    fout << "O" << endl;
  } else if (ans == rl) {
    fout << "L" << endl;
  } else if (ans == rs) {
    fout << "S" << endl;
  } else {
    fout << "O" << endl;
  }

  return 0;
}
