#include <fstream>

int main() {
  std::ifstream in("cifre.in");
  std::ofstream out("cifre.out");
  int n, c;
  in >> n >> c;
  long long r = 0;
  for (long long i = 1; i <= n; ++i) {
    long long x = i;
    while (x > 0) {
      if (x % 10 == c) { ++r; }
      x /= 10;
    }
  }
  out << r << std::endl;
  return 0;
}
