#include <fstream>
#include <vector>

using namespace std;

ifstream in("ozn.in");
ofstream out("ozn.out");

const int MAXN = 20005;

int n, k;
int x1[MAXN], y1[MAXN], x2[MAXN], y2[MAXN], nr[MAXN];
int a[MAXN];

int main() {
    in >> n >> k;
    for (int i = 1; i <= n; i++) {
        in >> x1[i] >> y1[i] >> x2[i] >> y2[i] >> nr[i];
    }
    for (int i = 1; i <= k; i++) {
        in >> a[i];
    }

    // for each weapon
    for (int i = 1; i <= k; i++) {
        int sum = 0;
        // for each ship
        for (int j = 1; j <= n; j++) {
            // if the ship is intersected by the weapon
            if (a[i] >= min(x1[j], x2[j]) && a[i] <= max(x1[j], x2[j])) {
                sum += nr[j];
            }
        }
        out << sum << '\n';
    }

    return 0;
}
