def is_prime(num):
    if num <= 1:
        return False
    if num <= 3:
        return True
    if num % 2 == 0 or num % 3 == 0:
        return False
    i = 5
    while i * i <= num:
        if num % i == 0 or num % (i + 2) == 0:
            return False
        i += 6
    return True

def sum_of_digits(num):
    return sum(int(digit) for digit in str(num))

def calculate_duration(h1, m1, h2, m2):
    start = h1 * 60 + m1
    end = h2 * 60 + m2
    if end < start:
        end += 24 * 60
    return end - start

def find_max_duration(flights, reverse_special=False):
    max_duration = 0
    for flight in flights:
        a1, a2, h1, m1, h2, m2 = flight
        if reverse_special and is_prime(a1) and a2 % sum_of_digits(a1) == 0:
            duration = calculate_duration(h2, m2, h1, m1)
        else:
            duration = calculate_duration(h1, m1, h2, m2)
        max_duration = max(max_duration, duration)
    return max_duration

def process_flights():
    with open('aeriana.in', 'r') as file:
        c = int(file.readline().strip())
        n = int(file.readline().strip())
        flights = [list(map(int, file.readline().split())) for _ in range(n)]

    reverse_special = (c == 2)
    max_duration = find_max_duration(flights, reverse_special=reverse_special)

    hours, mins = divmod(max_duration, 60)

    with open('aeriana.out', 'w') as file:
        file.write(f"{hours} {mins}\n")

process_flights()

