#include <fstream>

using namespace std;

int main() {
    // Init arrays for the 3 grades of relation and m/f/ch count.
    int fam[3] = {0, 0, 0}, count[3] = {0, 0, 0};
    int n, v;
    ifstream fin("case.in");
    ofstream fout("case.out");
    fin >> n;
    for (int i = 0; i < n; ++i) {
        fin >> v;
        fam[v / 1000 - 1]++;              // Extract the first digit of the house number.
        count[0] += v % 1000 / 100;       // Extract the second digit of the house number.
        count[1] += v % 100 / 10;         // Extract the third digit of the house number.
        count[2] += v % 10;                // Extract the last digit of the house number.
    }
    fin.close();
    for (auto f : fam)
        fout << f << '\n';
    for (auto f : count)
        fout << f << '\n';
    fout.close();
    return 0;
}
