#include <cstdio>
#include <vector>

const int maxn = 500, maxd = 500, maxk = 500;
long long  dp[maxn + 1][maxd + 1];

int main() {
  freopen("expozitie.in", "rt", stdin);
  freopen("expozitie.out", "wt", stdout);

  int n, d, k;
  scanf("%d %d %d", &n, &d, &k);
  for (int i = 0; i <= k; ++i) { dp[i][0] = dp[i][1] = 1; }

  for (int i = 2; i <= d; ++i) {
    for (int j = k; j <= n; ++j) {
      dp[j][i] = (dp[j][i - 1] * i % 2147483647) % 2147483647;
      if (j > k) { dp[j][i] = (dp[j][i] + dp[j - k][i]) % 2147483647; }
    }
  }
  printf("%lld\n", dp[n][d]);
  return 0;
}
