#include <fstream>

using namespace std;

int main() {
  ifstream fin("cartonase.in");
  ofstream fout("cartonase.out");
  int c, n, p = 0, maxs = 0, count = 0, maxc = 0;
  fin >> c >> n;
  int s[n], d[n];
  for (int i = 0; i < n; i++) fin >> s[i] >> d[i];
  for (int i = 0; i < n - 1; i++)
    if (d[i] == s[i + 1]) p++;
  for (int i = 0; i < n - 1; i++) {
    count = 0;
    for (int j = i; j < n - 1; j++)
      if (d[j] == s[j + 1])
        count++;
      else
        break;
    if (count > maxs) maxs = count;
  }
  count = 0;
  for (int i = 0; i < n - 1; i++)
    if (d[i] == s[i + 1])
      count++;
    else
      count = 0;
  maxs = max(maxs, count);
  for (int i = 0; i < n - 1; i++)
    if (d[i] == s[i + 1])
      count++;
    else {
      if (count == maxs) maxc++;
      count = 0;
    }
  if (count == maxs) maxc++;
  fout << (c == 1 ? p : (c == 2 ? maxs + 1 : maxc)) << "\n";
  return 0;
}
