import sys

# Read input
with open("lant.in", "r") as f:
    k = int(f.readline())
    text = f.read().split()

# Build the graph
graph = {}
for word in text:
    if word not in graph:
        graph[word] = set()

for word1, word2 in zip(text, text[1:]):
    if word1 not in graph:
        graph[word1] = set()
    diff = 0
    for c1, c2 in zip(word1, word2):
        if c1 != c2:
            diff += 1
    if diff <= k:
        graph[word1].add(word2)

# Find connected components
components = []
visited = set()

def dfs(word):
    if word in visited:
        return
    visited.add(word)
    components[-1].add(word)
    for neighbor in graph[word]:
        dfs(neighbor)

for word in graph:
    if word not in visited:
        components.append(set())
        dfs(word)

# Count the number of chains
count = 0
for component in components:
    if text[0] in component:
        count += len(component) - 1

# Output result
with open("lant.out", "w") as f:
    f.write(str(count))
